package patterns;

public class rectangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 8,m=5;
		for (int i = 1; i <= n; i++) {
			for (int j = 1; j <= m; j++) {
				System.out.print(" * ");
			}
			System.out.println();
		}

	}
}




